import{_ as p,c as e}from"./app.ec4f13d9.js";const t={},c=e("<p>\u603B\u5F97\u6709\u5730\u65B9\u6765\u5B58\u6211\u5F53\u5E74\u5199\u7684\u5C0F\u4F5C\u6587\u3002</p><hr><p>\u9AD8\u8003\u4E4B\u524D\uFF0C\u6211\u60F3\u201C\u7B49\u6211\u8003\u5B8C\u540E\u6211\u8981\u5199\u4E00\u7BC7\u9AD8\u4E2D\u751F\u6D3B\u56DE\u5FC6\u5F55\u201D\u3002\u672C\u6765\u662F\u51C6\u5907\u53BB\u81EA\u4E3B\u62DB\u751F\u7684\u65F6\u5019\u5728\u7B49\u98DE\u673A/\u7B49\u8F66/\u7B49\u8003\u8BD5\u5F00\u59CB/\u7B49\u522B\u7684\u4EC0\u4E48\u4E1C\u897F/\u9664\u4E86\u53D1\u5446\u6CA1\u4EC0\u4E48\u53EF\u5E72\u65F6\u7801\u4E00\u7BC7\u51FA\u6765\uFF0C\u4E0D\u8FC7\u8FD9\u4E9B\u65F6\u95F4\u90FD\u88AB\u6211\u7528\u6765\u7801\u53E6\u4E00\u7BC7\u201C\u975E\u5E38\u9634\u6697\u7248\u672C\u7684\u9AD8\u4E2D\u751F\u6D3B\u56DE\u5FC6\u5F55\u201D\uFF08\u867D\u7136\u6211\u6700\u5F00\u59CB\u7ED9\u5B83\u8D77\u7684\u540D\u5B57\u662F\u201C\u5149\u8F89\u4EBA\u751F\u56DE\u5FC6\u5F55\u201D\uFF09\u4E86\uFF0C\u8FD9\u4E2A\u7248\u672C\u9634\u6697\u5F97\u6211\u73B0\u5728\u90FD\u4E0D\u60F3\u518D\u770B\u4E00\u904D...</p><p>\u7136\u540E\u6211\u51B3\u5B9A\u5728\u6211 18 \u5C81\u751F\u65E5\u7684\u65F6\u5019\u5199\u4E00\u7BC7\u51FA\u6765\uFF0C\u53EF\u662F\u6211\u61D2\uFF0C\u6240\u4EE5...</p><p>\u5982\u4ECA\u53C8\u4E00\u5E74\u7684\u9AD8\u8003\u90FD\u5DF2\u7ECF\u8003\u5B8C\u4E86\uFF0C\u6211\u5C31\u8981\u671F\u672B\u8003\u8BD5\uFF0C\u8BB8\u591A ddl \u6CA1\u8D76\u5B8C\uFF0C\u8BB8\u591A\u4E1C\u897F\u60F3\u5B66\u6CA1\u5B66\u4F1A\uFF0C\u53EF\u6211\u5C45\u7136\u5728\u5199\u8FD9\u4E2A\u4E00\u5E74\u524D\u5C31\u8BE5\u5B8C\u6210\u7684\u4E1C\u897F\uFF0Corz...</p><hr><p>\u6211\u5927\u6982\u4ECE\u6765\u5C31\u4E0D\u662F\u5B66\u4E60\u5F97\u8FDB\u53BB\u7684\u4EBA\uFF0C\u6211\u9AD8\u4E2D\u4E09\u5E74\u5B66\u4E60\u6700\u52AA\u529B\u7684\u65F6\u5019\uFF0C\u662F\u9AD8\u4E00\u4E0A\u5B66\u671F\u6362\u5EA7\u4F4D\u6362\u5230\u4E00\u4E2A\u6570\u7ADE\u5927\u5927\u5927\u5927\u5927\u5927\u5927\u795E\u7684\u540E\u9762\uFF0C\u7136\u540E\u5305\u62EC\u6211\u5728\u5185\u7684\u5750\u5728\u5927\u795E\u5468\u56F4\u7684\u4EBA\u5C31\u76EE\u7779\u4E86\u4ED6\u4E24\u5929\u505A\u5B8C\u4E86\u672C\u6765\u8BE5\u4E00\u4E2A\u5B66\u671F\u505A\u5B8C\u7684\u7EC3\u4E60\u518C...\u7136\u540E\u6211\u4EEC\u5C31\u50CF\u6253\u4E86\u9E21\u8840\u4E00\u6837\u62FC\u547D\u7684\u9884\u4E60\u548C\u5237\u9898\uFF08\u6211\u5F53\u65F6\u628A\u8FD9\u79F0\u4E4B\u4E3A\u201C\u519B\u5907\u7ADE\u8D5B\u201D\uFF09\uFF0C\u5F53\u7136\u8FD9\u9E21\u8840\u7684\u6548\u679C\u5E76\u6CA1\u6709\u6301\u7EED\u592A\u4E45...</p><p>\u65E2\u7136\u5B66\u4E0D\u8FDB\u53BB\uFF0C\u90A3\u80FD\u4E0D\u5B66\u7684\u65F6\u5019\u5C31\u4E0D\u5B66\u5427\u3002\u4E8E\u662F\u6211\u4E0B\u8BFE\u540E\u8981\u662F\u56F0\u5C31\u7761\u89C9\uFF0C\u4E0D\u56F0\u5C31\u5728\u6559\u5BA4\u5916\u9762\u6643\u6765\u6643\u53BB\uFF0C\u53CD\u6B63\u4E0D\u60F3\u5B66\u4E60\u3002\u5230\u4E86\u9AD8\u4E8C\u9AD8\u4E09\u5DF2\u7ECF\u5230\u4E86\u8981\u662F\u54EA\u8282\u8BFE\u4E0B\u4E86\u4E0D\u51FA\u53BB\u6643\u5C31\u6D51\u8EAB\u4E0D\u8212\u670D\u7684\u5730\u6B65\uFF0C\u6240\u4EE5\u5F53\u65F6\u7279\u522B\u559C\u6B22\u505A\u9760\u8D70\u5ECA\u6216\u6700\u540E\u4E00\u6392\u7684\u4F4D\u7F6E\uFF0C\u56E0\u4E3A\u8FD9\u6837\u5C31\u4E0D\u4F1A\u9762\u4E34\u4E0B\u4E86\u8BFE\u60F3\u51FA\u53BB\u901B\u800C\u4E24\u8FB9\u540C\u684C\u90FD\u5728\u7761\u89C9\u4E0D\u60F3\u53EB\u9192\u4ED6\u4EEC\u7684\u5C34\u5C2C...</p><p>\u9AD8\u4E00\u9AD8\u4E8C\u7684\u65F6\u5019\u6559\u5BA4\u8FD8\u5728\u81F3\u5584\u697C\u4E00\u697C\uFF0C\u81F3\u5584\u697C\u524D\u662F\u4E00\u5757\u8FD8\u7B97\u5DE8\u5927\u7684\u5E73\u5730\uFF0C\u79CD\u4E86\u5F88\u591A\u6811\uFF0C\u6559\u5BA4\u95E8\u53E3\u5C31\u662F\u90A3\u9897\u5DF4\u8700\u5F15\u4EE5\u4E3A\u50B2\u7684\u83E9\u63D0\uFF0C\u867D\u7136\u957F\u5F97\u4F3C\u4E4E\u4E0D\u592A\u597D\u3002\u603B\u4E4B\u90A3\u5C31\u662F\u6211\u6643\u4E86\u4E24\u5E74\u7684\u5730\u65B9\uFF0C\u6211\u76F8\u4FE1\u6709\u5F88\u591A\u4EBA\u867D\u7136\u4E0D\u8BA4\u8BC6\u6211\uFF0C\u4F46\u770B\u5230\u6211\u540E\u4E5F\u4F1A\u77E5\u9053\u201C\u5C31\u662F\u8FD9\u4E2A\u50BB\u5B50\u5929\u5929\u5728\u697C\u4E0B\u6643\u201D\u3002\u6709\u4E00\u6B21\u4E00\u4E2A\u9694\u58C1\u73ED\u7684\u3001\u521D\u4E09\u65F6\u6559\u5BA4\u5728\u4E94\u697C\u7684\u5973\u751F\u8DDF\u6211\u8BF4\uFF1A\u201C\u4E00\u5E74\u524D\u6211\u8D70\u51FA\u6559\u5BA4\uFF0C\u770B\u5230\u7684\u662F\u6811\u9876\uFF0C\u800C\u73B0\u5728\u6211\u8D70\u51FA\u6559\u5BA4\u540E\u5374\u7AD9\u5728\u6811\u5E95\u201D\u3002\u7136\u540E\u6211\u60F3\u4E86\u60F3\uFF0C\u521D\u4E09\u65F6\u6211\u5728\u56DB\u697C\uFF0C\u4E0B\u8BFE\u540E\u4F1A\u8DB4\u5728\u8D70\u5ECA\u7684\u680F\u6746\u4E0A\uFF0C\u6709\u65F6\u4F1A\u6709\u4E00\u4E2A\u5F88\u6709\u54F2\u601D\u7684\u540C\u5B66\u8DDF\u6211\u51FA\u6765\u804A\u4E00\u4E9B\u53EF\u80FD\u6211\u4E5F\u4E0D\u77E5\u9053\u662F\u4EC0\u4E48\u7684\u95EE\u9898\uFF0C\u53CD\u6B63\u6211\u4ECE\u6765\u6CA1\u6709\u5173\u6CE8\u8FC7\u90A3\u68F5\u83E9\u63D0\uFF0C\u90A3\u65F6\u5019\u5DF4\u8700\u8FD8\u5728\u7FFB\u4FEE\uFF0C\u83E9\u63D0\u521A\u88AB\u79FB\u690D\u8FC7\u6765\uFF0C\u957F\u5F97...\u5443...\u633A\u51C4\u51C9\u7684...</p><p>\u9AD8\u4E09\u65F6\u6211\u4EEC\u5C31\u88AB\u5173\u8FDB\u4E86\u90A3\u4E2A\u5929\u4E95\u5F0F\u7684\u6559\u5B66\u697C\uFF0C\u4E8E\u662F\u4E0B\u8BFE\u540E\u6211\u5C31\u53EA\u80FD\u8DB4\u5728\u6559\u5BA4\u95E8\u53E3\u56F4\u57CE\u4E00\u5708\u7684\u680F\u6746\u4E0A\u53D1\u5446\uFF0C\u6211\u7ECF\u5E38\u8DB4\u7684\u4F4D\u7F6E\u6B63\u5BF9\u6211\u4EEC\u73ED\u548C\u9694\u58C1\u73ED\u7684\u6559\u5BA4\uFF0C\u4E8E\u662F\u6211\u53D1\u7740\u5446\u770B\u7740\u540C\u5B66\u4EEC\u4ECE\u6559\u5BA4\u95E8\u51FA\u6765\u53C8\u8FDB\u53BB\uFF0C\u4E00\u5E74\u4E0B\u6765\u5BF9\u9694\u58C1\u73ED\u5F88\u591A\u540C\u5B66\u4E86\u89E3\u4E86\u4E0D\u5C11...\u867D\u7136\u8FD9\u5E76\u6CA1\u6709\u4EC0\u4E48\u7528...\u6211\u4E00\u8FB9\u65E0\u6240\u4E8B\u4E8B\u4E00\u8FB9\u671F\u5F85\u6709\u4EBA\u4F1A\u4ECE\u67D0\u4E2A\u6559\u5BA4\u91CC\u8D70\u51FA\u6765\u968F\u4FBF\u7528\u4EC0\u4E48\u65B9\u5F0F\u544A\u8BC9\u6211\u6211\u6240\u60F3\u7684\u6240\u505A\u7684\u5979\u90FD\u80FD\u7406\u89E3\u3002\u6211\u60F3\u90A3\u4E2A\u65F6\u5019\u6211\u5927\u6982\u771F\u7684\u5F88\u5B64\u72EC\uFF0C\u65E2\u7136\u5B64\u72EC\u90A3\u4E0D\u5982\u6EDA\u53BB\u5B66\u4E60\uFF0C\u53EF\u6211\u4E0D\u60F3\u5B66\u4E60\uFF0C\u4E8E\u662F\u628A\u5927\u628A\u7684\u65F6\u95F4\u90FD\u8017\u5728\u795E\u6E38\u4E0E\u53D1\u5446\u4E0A\uFF0C\u8017\u5728\u4EF0\u5934\u900F\u8FC7\u697C\u9876\u7684\u73BB\u7483\u770B\u5149\u5F71\u53D8\u5E7B\u4E0A\uFF0C\u8017\u5728\u548C\u9694\u58C1\u73ED\u7684\u5BA4\u53CB\u7AD9\u5728\u529E\u516C\u5BA4\u524D\u7684\u8D70\u5ECA\u4E0A\u770B\u6811\u7684\u53F6\u5B50\u7531\u7EFF\u53D8\u9EC4\u6700\u540E\u6389\u5149\u4E0A\uFF0C\u8017\u5728\u601D\u8003\u201C\u6211\u4E3A\u4EC0\u4E48\u662F\u4E00\u4E2A\u4EBA\u5462\u201D\u201C\u8BF4\u4E0D\u5B9A\u6211\u4E0D\u662F\u4E00\u4E2A\u4EBA\u5462\u201D\u201C\u662F\u4E2A\u795E\u4E5F\u4E0D\u4E00\u5B9A\u5462\u201D\u8FD9\u79CD\u7EC8\u6781\u54F2\u5B66\u95EE\u9898\u4E0A...</p><p>\u53EF\u80FD\u652F\u6491\u6211\u8FC7\u5B8C\u9AD8\u4E09\u7684\u5C31\u662F\u8FD9\u6837\u7684\u65E0\u6240\u4E8B\u4E8B\u5427\u3002\u9AD8\u4E8C\u9AD8\u4E09\u8FC7\u5F97\u633A\u618B\u5C48\u7684\uFF0C\u53D1\u751F\u4E86\u5F88\u591A\u4E8B\uFF0C\u60F9\u5230\u4E86\u5F88\u591A\u4EBA\u3002\u8FD9\u4E9B\u4E8B\u548C\u4EBA\u5728\u5FC3\u91CC\u4E71\u6210\u4E00\u56E2\u5F88\u96BE\u53D7\uFF0C\u53EF\u6211\u4E5F\u4E0D\u6562\u53BB\u7406\u6E05\u695A\uFF0C\u6211\u6015\u6211\u7406\u7740\u7406\u7740\u5C31\u5D29\u6E83\uFF0C\u6BD5\u7ADF\u9AD8\u4E8C\u7684\u65F6\u5019\u5C31\u6709\u4EBA\u5BF9\u6211\u8BF4\u8FC7\u300C\u6211\u89C9\u5F97\u4F60\u9AD8\u4E09\u4F1A\u53BB\u8DF3\u697C\u300D\u3002\u6211\u5927\u6982\u4E0D\u7B97\u662F\u4E00\u4E2A\u8BA8\u4EBA\u559C\u6B22\u7684\u4EBA\uFF0C\u53EF\u6211\u4E5F\u4E0D\u60F3\u53BB\u8DF3\u697C\uFF0C\u4E8E\u662F\u5C31\u8FD9\u6837\u65E0\u6240\u4E8B\u4E8B\u4EC0\u4E48\u4E5F\u4E0D\u60F3\u53CD\u800C\u6210\u4E86\u4E00\u79CD\u5E78\u798F\u3002\u7136\u800C\u60F3\u4E0D\u6E05\u695A\u90A3\u4E9B\u4E71\u6210\u4E00\u56E2\u7684\u4E8B\u5C31\u4F1A\u8BA9\u4EBA\u53D8\u5F97\u504F\u6267\u548C\u81EA\u6211\uFF0C\u8FD9\u79CD\u504F\u6267\u548C\u81EA\u6211\u5728\u9AD8\u8003\u524D\u5915\u7206\u53D1\uFF0C\u4E8E\u662F\u6211\u4F24\u5BB3\u5230\u4E86\u4E00\u4E2A\u5F53\u65F6\u633A\u559C\u6B22\u7684\u4EBA\u5E76\u641E\u50F5\u4E86\u5173\u7CFB\uFF0C\u55F7\u770B\u8D77\u6765\u6211\u771F\u662F\u79BD\u517D...</p><p>\u9664\u4E86\u5728\u5916\u9762\u53D1\u5446\u6211\u8FD8\u662F\u5E72\u4E86\u5F88\u591A\u4E8B\uFF0C\u6BD4\u5982\u7528\u90A3\u4E2A\u914D\u7F6E\u5F88\u4F4E\u5C4F\u5E55\u5F88\u5C0F\u7684\u5B89\u5353\u673A\u5F3A\u884C\u73A9\u4E86\u5F88\u591A\u6E38\u620F\u5E76\u628A\u5B83\u4EEC\u63A8\u8350\u7ED9\u4E86\u5BA4\u53CB\uFF1B\u6BD4\u5982\u753B\u4E86\u8BB8\u591A\u706B\u67F4\u4EBA\uFF1B\u6BD4\u5982\u4E0D\u77E5\u4E3A\u4F55\u8BA9\u540C\u5B66\u89C9\u5F97\u6211\u8981\u505A\u4E00\u4E2A\u201C\u70B8\u6389\u5DF4\u8700\u5927\u5E1D\u56FD\u201D\u7684\u6E38\u620F\uFF0C\u867D\u7136\u8FD9\u6E38\u620F\u76F4\u5230\u73B0\u5728\u90FD\u8FD8\u6CA1\u5F00\u59CB\u505A...\u597D\u5427\u770B\u8D77\u6765\u8FD9\u4E09\u5E74\u6211\u8FC7\u5F97\u6D51\u6D51\u5669\u5669\uFF0C\u8FD9\u6837\u7684\u6D51\u6D51\u5669\u5669\u53EA\u80FD\u8BA9\u6211\u4E0D\u65AD\u60F3\u8D77\u8FDB\u9AD8\u4E2D\u4E4B\u524D\u7684\u81EA\u5DF1\uFF0C\u90A3\u65F6\u5019\u6211\u89C9\u5F97\u81EA\u5DF1\u5200\u67AA\u4E0D\u5165\u767E\u6BD2\u4E0D\u4FB5\u5929\u4E0B\u65E0\u654C\uFF0C\u5E0C\u671B\u625B\u7740\u516B\u5341\u4E8C\u65A4\u7684\u5927\u5200\u8D70\u5357\u95EF\u5317\uFF0C\u543C\u7740\u6211\u4EEC\u7684\u706B\u8981\u628A\u4E16\u754C\u90FD\u70B9\u71C3...</p><p>\u9AD8\u4E2D\u5E72\u7684\u53E6\u5916\u4E00\u4EF6\u76EE\u524D\u770B\u6765\u8FD8\u4E0D\u7B97\u90A3\u4E48\u6D51\u6D51\u5669\u5669\u7684\u4E8B\u5C31\u662F\u53BB\u5B66\u7ADE\u8D5B\uFF0C\u7136\u800C\u4E00\u76F4\u4EE5\u6765\u5B66\u7ADE\u8D5B\u7684\u6700\u91CD\u8981\u7684\u76EE\u7684\u201C\u628A\u8BFE\u5916\u65F6\u95F4\u90FD\u82B1\u53BB\u5B66\u7ADE\u8D5B\u5C31\u6709\u7406\u7531\u4E0D\u5B66\u4E60\u4E86\uFF0C\u4E5F\u6709\u7406\u7531\u8003\u8BD5\u8003\u5DEE\u4E86\u201D\u4E5F\u633A\u6D51\u6D51\u5669\u5669\u7684\u3002\u90A3\u65F6\u603B\u6709\u540C\u5B66\u89C9\u5F97\u6211\u662F\u70ED\u7231\u8BA1\u7B97\u673A\u7684\u5927\u4F6C\uFF0C\u53EF\u6211\u771F\u7684\u5B66\u5F97\u5F88\u70C2\u4E5F\u6CA1\u90A3\u4E48\u70ED\u7231\u8BA1\u7B97\u673A...\u5728\u7ADE\u8D5B\u673A\u623F\u7684\u65F6\u5019\u6211\u5176\u5B9E\u4E5F\u5F88\u9893\u5E9F\uFF0C\u6211\u77E5\u9053\u81EA\u5DF1\u8BE5\u5237\u9898\u8BE5\u80CC\u6A21\u677F\u8BE5\u5B66\u65B0\u7B97\u6CD5\uFF0C\u53EF\u5C31\u662F\u514B\u5236\u4E0D\u4F4F\u7684\u5750\u5728\u7535\u8111\u524D\u53D1\u5446\u3002\u53EF\u5C31\u7B97\u662F\u8FD9\u6837\uFF0CNOIP \u6210\u7EE9\u51FA\u6765\u524D\u7684\u90A3\u4E2A\u665A\u4E0A\uFF0C\u6211\u4EE5\u4E3A\u6211\u6CA1\u62FF\u5230\u7701\u4E00\uFF0C\u5C45\u7136\u8FD8\u54ED\u4E86\uFF0C\u54ED\u5F97\u83AB\u540D\u5176\u5999\u3002\u6211\u660E\u660E\u4E0D\u8BE5\u5728\u610F\u8FD9\u4E2A\uFF0C\u6211\u5B81\u613F\u53D1\u5446\u90FD\u4E0D\u613F\u610F\u5199\u4EE3\u7801\uFF0C\u53C8\u6709\u4EC0\u4E48\u8D44\u683C\u5728\u610F\u6210\u7EE9\u3002\u7B80\u76F4\u4E0D\u53EF\u7406\u55BB\uFF0C\u6CA1\u6709\u6295\u5165\u4EC0\u4E48\u52AA\u529B\u5374\u8981\u6709\u90A3\u79CD\u201C\u6211\u5929\u4E0B\u65E0\u654C\u6CA1\u6709\u4EC0\u4E48\u505A\u4E0D\u5230\u201D\u7684\u6240\u8C13\u9A84\u50B2\u3002\u7701\u9009\u524D\u7684\u90A3\u51E0\u5929\u4E5F\u77E5\u9053\u81EA\u5DF1\u8BE5\u628A\u5404\u79CD\u5E38\u8003\u7684\u677F\u90FD\u80CC\u4E00\u7BC7\uFF0C\u6BD5\u7ADF\u4E4B\u524D\u53EF\u6301\u4E45\u5316\u7EBF\u6BB5\u6811\u83AB\u6BD4\u4E4C\u65AF\u53CD\u6F14\u4EC0\u4E48\u7684\u5C31\u57FA\u672C\u6CA1\u5728\u4E0D\u770B\u677F\u7684\u60C5\u51B5\u4E0B\u5199\u51FA\u6765\u8FC7\uFF0C\u53EF\u6211\u4F9D\u7136\u6CA1\u53BB\u80CC\u3002\u7701\u9009\u524D\u4E00\u5929\u53BB\u8003\u573A\u8E29\u70B9\u8BD5\u673A\u5B50\uFF0C\u9694\u58C1\u516B\u4E2D\u7684\u4EBA\u5728\u673A\u5B50\u4E0A\u6572\u4E86\u4E00\u904D Splay\uFF0C\u800C\u6211\u5728\u673A\u5B50\u4E0A\u6253\u5F00\u4E86\u626B\u96F7\u3002\u4E8E\u662F\u7701\u9009\u7684\u65F6\u5019\u770B\u7740\u4E94\u9053\u9898\u91CC\u9762\u4E00\u9053\u53EF\u6301\u4E45\u5316\u7EBF\u6BB5\u6811\u4E00\u9053\u83AB\u6BD4\u4E4C\u65AF\u53CD\u6F14\u5F53\u573A\u5FC3\u6001\u5C31\u5D29\u4E86\uFF0C\u5D29\u5F97\u8FDE\u7F51\u7EDC\u6D41\u90FD\u4E00\u5206\u6CA1\u5F97\uFF0C\u7406\u6240\u5F53\u7136\u7684\u6CA1\u8FDB\u7701\u961F\uFF0C\u6EDA\u56DE\u53BB\u5168\u804C\u51C6\u5907\u9AD8\u8003\u3002</p><p>\u521A\u9000\u7ADE\u8D5B\u7684\u65F6\u5019\u662F\u9AD8\u4E8C\u4E0B\u5B66\u671F\uFF0C\u6210\u7EE9\u7206\u70B8\uFF0C\u6392\u540D\u51E0\u767E\u540D\u5F00\u5916\uFF0C\u4E00\u8F6E\u590D\u4E60\u90FD\u5FEB\u5F00\u59CB\u4E86\uFF0C\u800C\u6211\u8FD8\u7F3A\u4E86\u5F88\u591A\u77E5\u8BC6\u70B9\u3002\u5C24\u5176\u662F\u5316\u5B66\uFF0C\u57FA\u672C\u5C31\u8DDF\u6CA1\u5B66\u8FC7\u4E00\u6837\uFF0C\u9AD8\u4E2D\u552F\u4E00\u4E00\u6B21\u4E0D\u53CA\u683C\u5C31\u662F\u521A\u9000\u7ADE\u8D5B\u7684\u65F6\u5019\u5316\u5B66\u8003\u4E86\u4E2A 53\u3002\u7136\u540E\u6211\u5B66\u7ADE\u8D5B\u7684\u76EE\u7684\u5C31\u8FBE\u6210\u4E86\uFF0C\u8FDE\u6211\u7238\u5988\u90FD\u89C9\u5F97\u6211\u662F\u56E0\u4E3A\u5B66\u4E86\u7ADE\u8D5B\u624D\u8FD9\u6837\u7684\u3002\u4E8B\u5B9E\u4E0A\u6211\u77E5\u9053\u4EE5\u6211\u90A3\u4E24\u5E74\u7684\u72B6\u6001\u5C31\u7B97\u4E0D\u5B66\u7ADE\u8D5B\u5927\u6982\u4E5F\u597D\u4E0D\u5230\u54EA\u91CC\u53BB\u3002\u4E00\u8F6E\u4E8C\u8F6E\u4E09\u8F6E\u590D\u4E60\u8DDF\u5B8C\u540E\u770B\u8D77\u6765\u5B66\u5F97\u5DEE\u4E0D\u591A\u4E86\uFF0C\u4F46\u80AF\u5B9A\u8FD8\u6709\u5F88\u591A\u5F88\u591A\u6211\u77E5\u9053\u6216\u4E0D\u77E5\u9053\u7684\u6F0F\u6D1E\uFF0C\u9B3C\u77E5\u9053\u9AD8\u8003\u4F1A\u8003\u5230\u54EA\u4E2A\u6F0F\u6D1E\u4E0A\u9762\uFF0C\u7136\u540E\u6211\u5C31\u6302\u4E86\u3002\u503C\u5F97\u6B23\u6170\u7684\u662F\u90A3\u5E74\u91CD\u5E86\u7B2C\u4E00\u5E74\u8003\u5168\u56FD\u5377\uFF0C\u9898\u76EE\u5C45\u7136\u51FA\u5947\u7684\u7B80\u5355\uFF0C\u4EE5\u81F3\u4E8E\u6211\u7269\u7406\u538B\u8F74\u7B97\u4E86\u4E09\u904D\uFF0C\u6BCF\u4E00\u904D\u90FD\u89C9\u5F97\u201C\u600E\u4E48\u4F1A\u90A3\u4E48\u7B80\u5355\uFF0C\u4E00\u5B9A\u662F\u6211\u54EA\u91CC\u641E\u9519\u4E86\u201D\uFF0C\u4E8E\u662F\u6F0F\u6D1E\u5E76\u6CA1\u6709\u66B4\u9732\u591A\u5C11\u3002</p><p>\u7136\u540E\u6211\u7684\u771F\u5B9E\u5206\u6570\u8FD8\u662F\u5F88\u96BE\u770B\uFF0C\u8DDF\u6211\u7684\u9884\u4F30\u5206\u5DEE\u4E86\u4E8C\u5341\u591A\u5206\uFF0C\u8FD9\u5206\u6570\u8BA9\u6211\u4E00\u8138\u61F5\u903C\u3002</p><p>\u770B\u5230\u6210\u7EE9\u7684\u65F6\u5019\u57FA\u672C\u5C31\u786E\u5B9A\u4E86\u5F97\u5728\u4E24\u4E2A\u81EA\u62DB\u8FC7\u4E86\u7684\u5B66\u6821\u91CC\u9009\u4E00\u4E2A\u3002\u4EE5\u6211\u5F53\u65F6\u7684\u6210\u7EE9\uFF0C\u540C\u6D4E\u662F\u6211\u80FD\u53BB\u7684\u6700\u597D\u7684\u5B66\u6821\uFF0C\u4E5F\u4E0D\u77E5\u9053\u6211\u662F\u600E\u4E48\u62FF\u5230\u964D\u5206\u7684\u3002\u540C\u6D4E\u81EA\u62DB\u5148\u8003\u5B9E\u9A8C\u518D\u9762\u8BD5\uFF0C\u5B9E\u9A8C\u662F\u6D4B\u4E0D\u660E\u6DB2\u4F53\u7684\u5BC6\u5EA6\uFF0C\u5927\u6982\u505A\u5F97\u4E0D\u600E\u4E48\u6837\u3002\u5B9E\u9A8C\u73AF\u8282\u63D0\u4F9B\u4E86\u8BA1\u7B97\u5668\uFF0C\u4F46\u6211\u5F53\u65F6\u4E0D\u4F1A\u7528\u79D1\u5B66\u8BA1\u7B97\u5668\uFF0C\u6572\u5B8C\u9664\u6CD5\u540E\u8BA1\u7B97\u5668\u4E0A\u4F1A\u51FA\u73B0\u4E00\u4E2A\u5206\u6570\uFF0C\u6211\u4E0D\u77E5\u9053\u8BE5\u600E\u4E48\u8C03\u6210\u5C0F\u6570\uFF0C\u4E8E\u662F\u6211\u6700\u540E\u8FD8\u662F\u6254\u6389\u4E86\u8BA1\u7B97\u5668\u5168\u7A0B\u624B\u7B97\u3002\u6211\u7684\u4E0A\u6D77\u5BA4\u53CB\uFF08\u4E0A\u6D77\u9AD8\u8003\u5141\u8BB8\u7528\u8BA1\u7B97\u5668\uFF09\u5728\u77E5\u9053\u4E86\u8FD9\u4EF6\u4E8B\u4EE5\u540E\uFF0C\u5632\u7B11\u4E86\u6211\u4E00\u987F\uFF0C\u5E76\u5728\u8003\u9AD8\u6570\u524D\u6559\u4F1A\u4E86\u6211\u79D1\u5B66\u8BA1\u7B97\u5668\u7684\u57FA\u672C\u7528\u6CD5\uFF08\u611F\u8C22\u5979\uFF09\u3002\u9762\u8BD5\u7684\u65F6\u5019\uFF0C\u9760\u7740\u201C\u8BF4\u8BDD\u5B8C\u5168\u4E0D\u8FC7\u8111\u201D\u548C\u201C\u4E00\u9762\u8BD5\u5C31\u80E1\u8BF4\u516B\u9053\u201D\u7EFC\u5408\u75C7\uFF0C\u6211\u57FA\u672C\u4E0A\u5BF9\u6BCF\u4E2A\u95EE\u9898\u90FD\u7ED9\u51FA\u4E86\u6B7B\u4EA1\u56DE\u7B54\u3002\u6BD4\u5982\u8001\u5E08\u95EE\uFF1A\u201C\u4F60\u4E3A\u4EC0\u4E48\u8981\u9009\u62E9\u8F6F\u4EF6\u5DE5\u7A0B\u4E13\u4E1A\u5462\u201D\uFF0C\u6211\uFF1A\u201C\u56E0\u4E3A\u6309\u7167\u540C\u6D4E\u7684\u653F\u7B56\uFF0C\u4FE1\u606F\u5B66\u7ADE\u8D5B\u9009\u624B\u53EA\u6709\u8F6F\u4EF6\u5DE5\u7A0B\u3001\u5DE5\u79D1\u8BD5\u9A8C\u73ED\u8BA1\u7B97\u673A\u5927\u7C7B\u548C\u98DE\u884C\u5668\u5236\u9020\u5DE5\u7A0B\u4E09\u4E2A\u9009\u62E9\uFF0C\u800C\u6211\u4E0D\u60F3\u9020\u98DE\u884C\u5668\uFF0C\u4E5F\u4E0D\u592A\u660E\u767D\u5DE5\u79D1\u8BD5\u9A8C\u73ED\u662F\u4EC0\u4E48\u201D\u3002\u603B\u4E4B\u6211\u73B0\u5728\u60F3\u5230\u6211\u9762\u8BD5\u7684\u65F6\u5019\u8BF4\u7684\u90A3\u4E9B\u83AB\u540D\u5176\u5999\u7684\u8BDD\u4F9D\u7136\u4F1A\u89C9\u5F97\u5C34\u5C2C\uFF0C\u8001\u5E08\u4EEC\u613F\u610F\u7ED9\u964D\u5206\u5B8C\u5168\u662F\u5927\u6069\u5927\u5FB7\u3002</p><p>\u73B0\u5728\u5728\u540C\u6D4E\u6211\u4E5F\u4E0D\u77E5\u8BF4\u4E0D\u8BF4\u5F97\u4E0A\u540E\u6094\uFF0C\u4F46\u8BA9\u6211\u518D\u9009\u4E00\u6B21\u6211\u80AF\u5B9A\u4F1A\u53BB\u53E6\u5916\u4E00\u4E2A\u5B66\u6821\u3002\u4F46\u53E6\u5916\u4E00\u4E2A\u5B66\u6821\u5728\u4EFB\u4F55\u4EBA\u770B\u6765\u90FD\u4E0D\u4F1A\u662F\u4E3B\u6D41\u9009\u62E9\uFF0C\u4E8E\u662F\u6211\u7238\u5988\u4E0D\u540C\u610F\uFF0C\u6211\u8DDF\u4ED6\u4EEC\u5435\uFF0C\u8DDF\u4ED6\u4EEC\u627E\u6765\u529D\u6211\u7684\u4EBA\u5435\uFF0C\u586B\u5FD7\u613F\u622A\u6B62\u7684\u90A3\u4E2A\u665A\u4E0A\u6211\u90FD\u60F3\u627E\u673A\u4F1A\u6539\u5FD7\u613F\u3002\u53EF\u6700\u540E\u8FD8\u662F\u89C9\u5F97\u7B97\u4E86\u5427\uFF0C\u6211\u9AD8\u4E2D\u4E09\u5E74\u51E0\u4E4E\u6CA1\u6709\u54EA\u4E00\u6B21\u8003\u8BD5\u6392\u540D\u80FD\u591F\u5F97\u4E0A\u540C\u6D4E\u7684\u5F55\u53D6\u7EBF\uFF0C\u518D\u600E\u4E48\u4E5F\u8F6E\u4E0D\u5230\u6211\u53BB\u5ACC\u5F03\u540C\u6D4E\u3002\u4E8E\u662F\u5C31\u8FD9\u6837\u5C18\u57C3\u843D\u5B9A\u3002</p><p>\u6211\u8BB0\u5F97\u9AD8\u4E2D\u65F6\u6211\u4E0D\u6B62\u4E00\u6B21\u505A\u5728\u673A\u623F\u7684\u7535\u8111\u524D\u4FE1\u8A93\u65E6\u65E6\u7684\u8BF4\u201C\u6211\u5927\u5B66\u80AF\u5B9A\u4E0D\u5B66\u8BA1\u7B97\u673A\u76F8\u5173\u4E13\u4E1A\u201D\uFF0C\u6210\u7EE9\u8FD8\u6CA1\u51FA\u6765\u7684\u65F6\u5019\u6211\u4E5F\u6309\u7167\u6211\u7684\u9884\u4F30\u5206\u770B\u4E86\u5F88\u591A\u5B66\u6821\u548C\u4E13\u4E1A\u3002\u800C\u6210\u7EE9\u51FA\u6765\u4EE5\u540E\uFF0C\u6211\u770B\u7684\u90A3\u4E9B\u5B66\u6821\u548C\u4E13\u4E1A\u90FD\u6CA1\u6709\u4E86\u610F\u4E49\uFF0C\u6700\u540E\u4F9D\u7136\u6CA1\u9003\u8FC7\u5F53\u7801\u519C\u7684\u547D\u8FD0\u3002\u6211\u4E5F\u4E0D\u77E5\u9053\u6211\u73B0\u5728\u8FDB\u4E86\u540C\u6D4E\u540E\u73B0\u5728\u662F\u4E2A\u4EC0\u4E48\u72B6\u6001\uFF0C\u53CD\u6B63\u4E0D\u662F\u4EC0\u4E48\u80FD\u8BA9\u6211\u5F81\u670D\u5B87\u5B99\u7684\u72B6\u6001\uFF0C\u4F46\u8FD8\u662F\u6BD4\u9AD8\u4E2D\u597D\u591A\u4E86\u3002</p><p>\u9AD8\u8003\u5B8C\u4EE5\u540E\u8003\u82F1\u8BED\u53E3\u8BED\uFF0C\u5F00\u653E\u6027\u9898\u76EE\u62BD\u5230\u4E86\u201C\u672A\u6765\u60F3\u505A\u7684\u804C\u4E1A\u201D\uFF0C\u6211\u4E00\u5F00\u53E3\u5C31\u8DD1\u706B\u8F66\uFF1A\u201Ctheoretical physicist, because theoretical physics is difficult, \u800C\u6211\u5C31\u559C\u6B22\u96BE\u7684\u30FD(\u02CB\u25BD\u02CA)\u30CE\u201D\uFF0C\u7136\u540E\u4FE9\u8003\u5B98\u5C31\u8089\u773C\u53EF\u89C1\u7684\u5F00\u59CB\u7B11\u3002\u53E3\u8BED\u8003\u5B8C\u540E\u53BB\u5BDD\u5BA4\u6536\u4E1C\u897F\uFF0C\u628A\u4E66\u90FD\u5806\u5728\u4E86\u5BDD\u5BA4\u95E8\u53E3\u8DDF\u5BBF\u7BA1\u963F\u59E8\u62FF\u53BB\u5356\u5E9F\u7EB8\u3002\u6211\u628A\u8FD8\u7B97\u770B\u5F97\u8FC7\u53BB\u7684\u51E0\u672C\u7B14\u8BB0\u5E26\u8D70\u4E86\uFF0C\u6BCF\u672C\u4E0A\u9762\u90FD\u6709\u6211\u753B\u7684\u706B\u67F4\u4EBA\u3002\u6211\u5BF9\u7740\u9AD8\u4E09\u65F6\u5199\u7684\u90A3\u672C\u65E5\u8BB0\u601D\u8003\u4E86\u4E00\u4F1A\u513F\uFF0C\u4E0A\u9762\u662F\u6211\u5373\u4F7F\u4E0D\u505A\u4F5C\u4E1A\u4E5F\u8981\u56FA\u6267\u7684\u7528\u6F66\u8349\u7684\u5B57\u8FF9\u548C\u6DF7\u6C8C\u7684\u8349\u56FE\u8BB0\u4E0B\u7684\u8BB0\u5FC6\u3001\u5E7B\u60F3\u3001\u75DB\u82E6\u4E0E\u68A6\u9B47\u4E00\u822C\u7684\u4FE1\u4EF0\u3002\u6700\u540E\u6211\u8FD8\u662F\u628A\u5B83\u5E26\u8D70\u4E86\uFF0C\u538B\u5728\u4E86\u5BB6\u91CC\u67DC\u5B50\u6DF1\u5904\u4E00\u5806\u6742\u7269\u4E0B\u9762\uFF0C\u6211\u81F3\u4ECA\u6CA1\u6709\u52C7\u6C14\u518D\u7FFB\u5F00\u5B83\uFF0C\u53EF\u80FD\u4EE5\u540E\u4E5F\u4E0D\u4F1A\u518D\u6709\u3002\u8D70\u7684\u65F6\u5019\u548C\u4EE5\u524D\u4EFB\u4F55\u4E00\u8D77\u671F\u672B\u8003\u5B8C\u6536\u4E1C\u897F\u56DE\u5BB6\u6CA1\u4EC0\u4E48\u533A\u522B\uFF0C\u5C31\u597D\u50CF\u8DDF\u6211\u4EEC\u5BDD\u5BA4\u7684\u9694\u58C1\u5BDD\u5BA4\u7684\u5BF9\u9762\u5BDD\u5BA4\u7684\u4EBA\u4EE5\u540E\u8FD8\u4F1A\u7ECF\u5E38\u89C1\u9762\u3002</p><p>\u7136\u800C\u5F88\u591A\u4EBA\u5176\u5B9E\u5C31\u518D\u4E5F\u6CA1\u6709\u89C1\u8FC7\u4E86\u3002</p><p>\u5C31\u7B97\u53D1\u751F\u4E86\u5F88\u591A\u4E8B\uFF0C\u5C31\u7B97\u6709\u7684\u4EBA\u4F60\u5DF2\u7ECF\u51B3\u5B9A\u4E0D\u518D\u53BB\u5173\u6CE8\uFF0C\u5DF2\u7ECF\u5728\u5FC3\u91CC\u5BF9\u5979\u9ED8\u5FF5\u4E86\u5F88\u591A\u904D\u201C\u6211\u4E0D\u4F1A\u518D\u6765\u6253\u6270\u4F60\u4E86\u201D\uFF0C\u79BB\u5F00\u7684\u65F6\u5019\u4F60\u4F9D\u7136\u4F1A\u5FCD\u4E0D\u4F4F\u56DE\u5934\u53BB\u518D\u770B\u4E00\u773C\u2014\u2014\u7136\u540E\u53D1\u73B0\u5979\u5DF2\u7ECF\u8D70\u4E86\u3002</p><p>\u7136\u540E\u4F60\u4EEC\u7684\u5173\u7CFB\u7A33\u5B9A\u5728\u6BCF\u5E74\u4E09\u6B21\u5BF9\u8BDD\uFF0C\u5206\u522B\u662F\u53CC\u65B9\u7684\u751F\u65E5\u548C\u65B0\u5E74\u3002</p><p>\u518D\u7136\u540E\u662F\u6BD5\u4E1A\u5178\u793C\uFF0C\u8FD9\u6B21\u6BD5\u4E1A\u5178\u793C\u5C45\u7136\u662F\u5728\u5F55\u53D6\u7ED3\u679C\u51FA\u6765\u524D\u529E\u7684\u300213\u300114 \u7EA7\u7684\u6BD5\u4E1A\u5178\u793C\u6211\u90FD\u53BB\u770B\u8FC7\uFF0C13 \u7EA7\u6BD5\u4E1A\u5178\u793C\u662F\u521D\u4E09\u6BD5\u4E1A\u53C2\u52A0\u590F\u4EE4\u8425\u7684\u65F6\u5019\u5B66\u6821\u7EC4\u7EC7\u53BB\u7684\uFF0C\u90A3\u4E00\u5E74\u5DF4\u8700\u8003\u4E0A\u6E05\u5317\u7684\u4EBA\u6570\u591A\u5E74\u4EE5\u6765\u7B2C\u4E00\u6B21\u6389\u4E0B\u4E86\u91CD\u5E86\u7B2C\u4E00\uFF0C\u6BD5\u4E1A\u5178\u793C\u4E0A\u5BA3\u4F20\u9AD8\u8003\u6210\u7EE9\u7684 PPT \u4E0A\u7684\u7528\u8BCD\u88AB\u8FEB\u4ECE\u201C\u5168\u5E02\u7B2C\u4E00\u201D\u6539\u6210\u4E86\u201C\u5168\u5E02\u9886\u5148\u201D\uFF0C\u5728\u559C\u5E86\u7684\u6BD5\u4E1A\u6C1B\u56F4\u91CC\u6211\u603B\u89C9\u5F97\u6211\u8FD8\u662F\u611F\u53D7\u5230\u4E86\u6821\u9886\u5BFC\u7684\u54AC\u7259\u5207\u9F7F\u300214 \u7EA7\u6BD5\u4E1A\u5178\u793C\u662F\u6691\u5047\u5728\u5B66\u6821\u5B66\u7ADE\u8D5B\u65F6\u88AB\u5F3A\u884C\u62C9\u53BB\u7684\uFF0C\u4E00\u4E2A\u73AF\u8282\u662F\u5148\u8BA9\u9AD8\u4E09\u6BD5\u4E1A\u751F\u7AD9\u8D77\u6765\uFF0C\u518D\u8BA9\u521D\u4E09\u6BD5\u4E1A\u6765\u53C2\u52A0\u590F\u4EE4\u8425\u7684\u5B66\u751F\u7AD9\u8D77\u6765\uFF0C\u800C\u6211\u4EEC\u4E0D\u7BA1\u54EA\u4E00\u6B21\u90FD\u4E0D\u7B26\u5408\u9700\u8981\u7AD9\u8D77\u6765\u7684\u6807\u51C6\uFF0C\u770B\u4E0A\u53BB\u957F\u5F97\u4E5F\u4E0D\u50CF\u5BB6\u957F\uFF0C\u56E0\u6B64\u5750\u5728\u6211\u4EEC\u5468\u56F4\u7684\u9AD8\u4E09\u524D\u8F88\u4EEC\u6295\u6765\u4E86\u56F0\u60D1\u7684\u76EE\u5149\u300215 \u7EA7\u6BD5\u4E1A\u5178\u793C\u6CA1\u6709\u95E8\u7968\u4E0D\u8BA9\u8FDB\u6240\u4EE5\u6CA1\u53BB\u6210\uFF0C\u90A3\u65F6\u6211\u4EEC\u9AD8\u4E8C\uFF0C\u770B\u7740\u5B66\u957F\u5B66\u59D0\u7A7F\u7740\u793C\u670D\u6D53\u5986\u8273\u62B9\u6B22\u4E50\u7684\u8D70\u7EA2\u6BEF\uFF0C\u89C9\u5F97\u79BB\u6BD5\u4E1A\u8FD8\u5F88\u9065\u8FDC\u3002\u6211\u4EEC\u8FD9\u5C4A\u7684\u6BD5\u4E1A\u5178\u793C\u5E72\u4E86\u4EC0\u4E48\u6211\u57FA\u672C\u90FD\u5FD8\u4E86\uFF0C\u53EA\u8BB0\u5F97\u6211\u5E2E\u4E00\u5806\u6CA1\u6765\u7684\u4EBA\u62FF\u4E86\u6BD5\u4E1A\u8BC1\u548C\u6BD5\u4E1A\u7167\uFF0C\u73ED\u4E3B\u4EFB\u5728\u77E5\u9053\u6211\u8FD9\u4E2A\u540A\u8F66\u5C3E\u80FD\u53BB\u540C\u6D4E\u4E4B\u540E\u9732\u51FA\u4E86\u6B23\u6170\u7684\u8868\u60C5\uFF0C\u5DF4\u8700\u5728\u5F55\u53D6\u7ED3\u679C\u6CA1\u51FA\u7684\u60C5\u51B5\u4E0B\u4F9D\u7136\u80FD\u5B8C\u7F8E\u7684\u5927\u5439\u7279\u5439\u3002</p><p>\u5927\u6982\u5C31\u662F\u8FD9\u6837\u5427\u3002</p><hr><p>\u8FD9\u4E00\u7248\u8BFB\u8D77\u6765\uFF0C\u5176\u5B9E\u8FD8\u662F\u5F88\u60B2\u50AC\u554A (\u256F\u2035\u25A1\u2032)\u256F\uFE35\u2558\u2550\u255B</p><p>P.S. \u8FD9\u4E2A\u6380\u684C\u7684\u8868\u60C5\u771F\u7684\u597D\u840C</p>",27);function r(i,s){return c}var a=p(t,[["render",r],["__file","index.html.vue"]]);export{a as default};
